INSERT INTO `zxznz_doctor` VALUES ('980', '187', '执业医师', '研究生', '男', '金隆秀', 'Doctor/2017-03-16/58c9f7dc477ef.jpg', '副主任医师', '0', '激光美肤、微整形、美体塑形', '0.00', '0', '', '&lt;p&gt;　翰林大学医疗院江南圣心医院教授(2005-2008)/ 翰林大学外来副教授 2007年，被任命为美国世界传记中心(ABI)名誉亲善大使 2006年，被任命为英国剑桥国际传记中心(IBC)整形外科部门的国际专门医 大韩整形外科学会终身会员 世界整形外科学会正式会员 大韩美容整形外科协会正式会员 斯迈尔医疗美容皮肤科院长&lt;/p&gt;', '2010 至今', '是', '0', '0');
